import React, { useState } from "react";
import { createUser } from "../api";

function Login({ onLogin }) {
  const [form, setForm] = useState({ username: "", email: "", password: "" });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await createUser(form);
      onLogin(res.data);
    } catch (err) {
      alert("Login failed: " + err.message);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center">
      <div className="card custom-card p-4 animate__animated animate__fadeInDown" style={{ width: "400px" }}>
        <h2 className="text-center mb-3">üîê CyberShield Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <input type="text" name="username" placeholder="Username"
              value={form.username} onChange={handleChange}
              className="form-control" required />
          </div>
          <div className="mb-3">
            <input type="email" name="email" placeholder="Email"
              value={form.email} onChange={handleChange}
              className="form-control" required />
          </div>
          <div className="mb-3">
            <input type="password" name="password" placeholder="Password"
              value={form.password} onChange={handleChange}
              className="form-control" required />
          </div>
          <button type="submit" className="btn btn-custom w-100">Login / Register</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
