import React, { useState } from "react";
import { detectThreat, getDetections } from "../api";

function Dashboard({ user }) {
  const [features, setFeatures] = useState("");
  const [result, setResult] = useState(null);
  const [detections, setDetections] = useState([]);

  const handleDetect = async () => {
    try {
      const featureArray = features.split(",").map(Number);
      const res = await detectThreat({
        src_ip: "192.168.1.10",
        dest_ip: "8.8.8.8",
        features: featureArray,
        threshold: 0.5,
      });
      setResult(res.data);
    } catch (err) {
      alert("Detection error: " + err.message);
    }
  };

  const handleGetDetections = async () => {
    try {
      const res = await getDetections();
      setDetections(res.data);
    } catch (err) {
      alert("Error fetching detections: " + err.message);
    }
  };

  return (
    <div className="animate__animated animate__fadeInUp">
      <div className="card custom-card p-4">
        <h2 className="mb-3">üìä CyberShield Dashboard</h2>
        <p className="text-muted">Welcome, {user.username}</p>

        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Enter features (e.g. 100,200,50,10,443,80)"
            value={features}
            onChange={(e) => setFeatures(e.target.value)}
          />
          <button onClick={handleDetect} className="btn btn-custom mt-2 w-100">
            Detect
          </button>
        </div>

        {result && (
          <div className="alert mt-3 animate__animated animate__fadeIn">
            <h5>Result:</h5>
            <p><b>ID:</b> {result.id}</p>
            <p><b>Malicious:</b> {result.is_malicious ? "‚ö†Ô∏è Yes" : "‚úÖ No"}</p>
            <p><b>Score:</b> {result.score}</p>
          </div>
        )}

        <div className="mt-4">
          <h4>Detection History</h4>
          <button onClick={handleGetDetections} className="btn btn-outline-info w-100">
            Load Detections
          </button>
          <ul className="list-group mt-3">
            {detections.map((d) => (
              <li key={d.id} className="list-group-item bg-dark text-white">
                {d.src_ip} ‚Üí {d.dest_ip} | {d.is_malicious ? "‚ö†Ô∏è Malicious" : "‚úÖ Safe"} | Score: {d.score}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
