import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";
import Register from "./components/Register";
import Blacklist from "./components/Blacklist";

function App() {
  const [user, setUser] = useState(null);
  const [tab, setTab] = useState("register"); // default Register
  const [prefill, setPrefill] = useState({ email: "", password: "" });

  if (!user) {
    return (
      <div className="container py-5">
        <div className="d-flex justify-content-center mb-4">
          <div className="btn-group shadow">
            <button
              className={`btn ${tab === "register" ? "btn-custom" : "btn-outline-light"}`}
              onClick={() => setTab("register")}
            >
              Register
            </button>
            <button
              className={`btn ${tab === "login" ? "btn-custom" : "btn-outline-light"}`}
              onClick={() => setTab("login")}
            >
              Login
            </button>
          </div>
        </div>

        {tab === "register" ? (
          <Register
            onRegistered={(email, password) => {
              setPrefill({ email, password });
              setTab("login");
            }}
          />
        ) : (
          <Login onLogin={(u) => setUser(u)} prefill={prefill} />
        )}
      </div>
    );
  }

  // When logged in ‚Üí Show nav tabs for Dashboard / Blacklist
  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="m-0">üõ°Ô∏è CyberShield</h3>
        <div className="btn-group">
          <button
            className={`btn btn-sm ${tab === "dashboard" ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setTab("dashboard")}
          >
            Dashboard
          </button>
          <button
            className={`btn btn-sm ${tab === "blacklist" ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setTab("blacklist")}
          >
            Blacklist
          </button>
          <button
            className="btn btn-sm btn-outline-light"
            onClick={() => {
              if (window.confirm("Logout?")) setUser(null);
            }}
          >
            Logout
          </button>
        </div>
      </div>

      {tab === "dashboard" ? <Dashboard user={user} /> : <Blacklist />}
    </div>
  );
}

export default App;
