import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import Login from "./components/Login";
import Register from "./components/Register";

function App() {
  const [user, setUser] = useState(null);
  const [tab, setTab] = useState("register"); // Register first
  const [prefill, setPrefill] = useState({ email: "", password: "" });

  if (user) {
    return (
      <div className="container py-5">
        <Dashboard user={user} />
      </div>
    );
  }

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-center mb-4">
        <div className="btn-group shadow">
          <button
            className={`btn ${tab === "register" ? "btn-custom" : "btn-outline-light"}`}
            onClick={() => setTab("register")}
          >
            Register
          </button>
          <button
            className={`btn ${tab === "login" ? "btn-custom" : "btn-outline-light"}`}
            onClick={() => setTab("login")}
          >
            Login
          </button>
        </div>
      </div>

      {tab === "register" ? (
        <Register
          onRegistered={(email, password) => {
            setPrefill({ email, password });
            setTab("login");
          }}
        />
      ) : (
        <Login onLogin={(u) => setUser(u)} prefill={prefill} />
      )}
    </div>
  );
}

export default App;
