import React, { useState } from "react";
import { detectThreat, getDetections } from "../api";

function Dashboard({ user }) {
  const [features, setFeatures] = useState("");
  const [result, setResult] = useState(null);
  const [detections, setDetections] = useState([]);

  const handleDetect = async () => {
    try {
      const featureArray = features.split(",").map(Number);
      const res = await detectThreat({
        src_ip: "192.168.1.10",
        dest_ip: "8.8.8.8",
        features: featureArray,
        threshold: 0.5,
      });
      setResult(res.data);
    } catch (err) {
      alert("Detection error: " + err.message);
    }
  };

  const handleGetDetections = async () => {
    try {
      const res = await getDetections();
      setDetections(res.data);
    } catch (err) {
      alert("Error fetching detections: " + err.message);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üìä CyberShield Dashboard</h2>
      <p>Welcome, {user.username}</p>

      <div>
        <h3>Run Threat Detection</h3>
        <input
          type="text"
          placeholder="Enter features (e.g. 100,200,50,10,443,80)"
          value={features}
          onChange={(e) => setFeatures(e.target.value)}
        />
        <button onClick={handleDetect}>Detect</button>
      </div>

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h3>Result:</h3>
          <p>ID: {result.id}</p>
          <p>Malicious: {result.is_malicious ? "‚ö†Ô∏è Yes" : "‚úÖ No"}</p>
          <p>Score: {result.score}</p>
        </div>
      )}

      <div style={{ marginTop: "30px" }}>
        <h3>Previous Detections</h3>
        <button onClick={handleGetDetections}>Load</button>
        <ul>
          {detections.map((d) => (
            <li key={d.id}>
              {d.src_ip} ‚Üí {d.dest_ip} | Malicious:{" "}
              {d.is_malicious ? "‚ö†Ô∏è" : "‚úÖ"} | Score: {d.score}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Dashboard;
